import pathlib

import pandas as pd
from scipy.interpolate import griddata

current_dir = pathlib.Path(__file__).parent
dac_data = pd.read_excel(io=current_dir.joinpath(r"dac_data.xlsx"))
relative_humidity = pd.read_excel(io=current_dir.joinpath(r"relative_humidity.xlsx"))

surface_air_temperature = pd.read_excel(
    io=current_dir.joinpath(r"surface_air_temperature.xlsx")
)


elec = griddata(
    (dac_data["T"], dac_data["RH"]),
    dac_data["totalElectricity"],
    (
        surface_air_temperature,
        relative_humidity,
    ),
    method="linear",
)
print(elec.tolist())
